function addToCart(n,t){document.contains(document.getElementById("addedItemToCart"))&&document.getElementById("addedItemToCart").remove();var i=$("#antiForgeryForm input[name=__RequestVerificationToken]").val(),r={productId:n,quantity:t};$.ajax({type:"POST",url:"/shoppingcart/addcartitem",data:JSON.stringify(r),dataType:"html",contentType:"application/json; charset=utf-8",headers:{RequestVerificationToken:i},success:function(n){var t=new bootstrap.Modal($(n));t.show();renewCartItemCount()}})}function getCartItems(){document.contains(document.getElementById("cartItems"))&&($("#cartItems").modal("hide"),document.getElementById("cartItems").remove());$.ajax({url:"/shoppingcart/getcartitems",dataType:"html",success:function(n){var t=new bootstrap.Modal($(n));t.show()}})}function removeCartItem(n){$.ajax({url:"/shoppingCart/RemoveCartItem/"+n,dataType:"text",success:function(t){var r=document.getElementById(n),i;r.remove();i=JSON.parse(t);document.getElementById("cartTotalPrice").innerText="$"+i.totalPrice;renewCartItemCount()}})}function renewCartItemCount(){$.ajax({url:"/shoppingCart/GetCartItemsCount/",dataType:"text",success:function(n){for(var t=JSON.parse(n),r=document.getElementsByClassName("lblCartCount"),i=0;i<r.length;i++)r[i].innerText=t.cartItemsCount;document.contains(document.getElementById("checkoutCartItemsCount"))&&(t.cartItemsCount==0&&$("#staticModalForEmptyCart").modal("show"),document.getElementById("checkoutCartItemsCount").innerText=t.cartItemsCount)}})}function clearCart(){$.ajax({url:"/shoppingCart/ClearCart/",dataType:"text",success:function(){renewCartItemCount();getCartItems()}})}function addToCartWithQuantity(n){var i=document.getElementById("quantityValue"),t=parseInt(i.value);t>=1&&addToCart(n,t)}function drawAreaChart(n,t){var i=JSON.parse(n),r,u,o;console.log(i);r=i.days;u=i.sales;const f={labels:r,datasets:[{fill:!1,label:"Sales",backgroundColor:"rgb(255, 99, 132)",borderColor:"rgb(255, 99, 132)",data:u}]},e={type:"line",responsive:!0,data:f,options:{plugins:{filler:{propagate:!1}},interaction:{intersect:!1}}};o=new Chart(document.getElementById(t),e)}function drawBarChart(n,t){var i=JSON.parse(n),r,u,o;console.log(i);r=i.days;u=i.sales;const f={labels:r,datasets:[{fill:!1,label:"Sales",backgroundColor:"#3399FF",borderColor:"#3399FF",data:u}]},e={type:"bar",data:f,responsive:!0,options:{plugins:{filler:{propagate:!1}},interaction:{intersect:!1},title:{display:!0,text:"Sales"}}};o=new Chart(document.getElementById(t),e)}