function addToCart(n,t){document.contains(document.getElementById("addedItemToCart"))&&document.getElementById("addedItemToCart").remove();var i=$("#antiForgeryForm input[name=__RequestVerificationToken]").val(),r={productId:n,quantity:t};$.ajax({type:"POST",url:"/shoppingcart/addcartitem",data:JSON.stringify(r),dataType:"html",contentType:"application/json; charset=utf-8",headers:{RequestVerificationToken:i},success:function(n){var t=new bootstrap.Modal($(n));t.show();renewCartItemCount()}})}function getCartItems(){document.contains(document.getElementById("cartItems"))&&document.getElementById("cartItems").remove();$.ajax({url:"/shoppingcart/getcartitems",dataType:"html",success:function(n){var t=new bootstrap.Modal($(n));t.show()}})}function removeCartItem(n){$.ajax({url:"/shoppingCart/RemoveCartItem/"+n,dataType:"text",success:function(t){var r=document.getElementById(n),i;r.remove();i=JSON.parse(t);document.getElementById("cartTotalPrice").innerText="$"+i.totalPrice;renewCartItemCount()}})}function renewCartItemCount(){$.ajax({url:"/shoppingCart/GetCartItemsCount/",dataType:"text",success:function(n){var t=JSON.parse(n);console.log(n);console.log(t.cartItemsCount);console.log(document.getElementById("lblCartCount"));document.getElementById("lblCartCount").innerText=t.cartItemsCount}})}